  let login = document.getElementById('login');
 if(login!=null){
  login.innerHTML=""  
  login.parentNode.removeChild(login);

  solicitud(token)
 

 // solicitudes(token)
}
 return(
 <React.Fragment>
 <h1>Admin Page</h1>
 <fieldset id="solicitudes">
     
