import React, {useState} from "react"
import { useForm } from "react-hook-form";
import {getDatos, deleteAcount, buySeguro} from "../axios/userServices";
import { useAuth } from "../commons/auth/auth-context";

function User() {
  const [isFormOpen, setIsFormOpen] = useState(false);

  const {token} = useAuth();


  let login = document.getElementById('login');
 if(login!=null){
  login.innerHTML=""  
  login.parentNode.removeChild(login);
}



  Datos()
  return (
      <React.Fragment>
        <section id="user">
  <span id="datos">
  <h1>Mis Datos:</h1>
  <fieldset>
  <h2 id="first_name"></h2>
    <h3 id="last_name"></h3>
  </fieldset>
  <fieldset>
    <label>Phone:</label>
<label id="phone"></label>
  </fieldset>
  <fieldset>
    <label>email:</label>
<label id="email"></label>
  </fieldset>
  <fieldset>
    <label>gender:</label>
<label id="gender"></label>
  </fieldset>
  <fieldset>
    <label>Direccion:</label>
    <label id="address"></label>
  </fieldset>
  <fieldset>
    <label>Fecha de nacimiento:</label>
<label id="dob"></label>
  </fieldset>
  <fieldset>
    <label>Born IN:</label>
<label id="born_in"></label>
  </fieldset>
  <fieldset>
    <label>Codigo Postal:</label>
<label id="postal_code"></label>
  </fieldset>
  </span>
          <span id="solicitud">
            <h1>Solicitar asistencia:</h1>
            <fieldset>
              <h2>Titulo:</h2>
              <input type="text" id="title" />
            </fieldset>
            <fieldset>
              <h3>Descripcion</h3>
              <textarea id="description" />
            </fieldset>
            <fieldset>
              <h3>Precio Estimado</h3>
              <input type="number" />
            </fieldset>
            <fieldset>
              <button>Enviar</button>
            </fieldset>
          </span>
          <span id="acciones">
            <h1>Acciones</h1>
            <fieldset>
              <button >Cambiar Datos</button>
              <button >Contratar Seguro</button>


            </fieldset>
            <fieldset>
            <button class="danger"> Dar de baja poliza</button>
              <button class="danger" id="danger" onClick={()=>del(token)} >Cancelar Cuenta</button>
            </fieldset>
          </span>
        </section>

      </React.Fragment>
    );
  }


export default User;


async function del(token){
  let respon = await deleteAcount(token)
    if(await respon ){
      window.location.href="https://segurosalud.herokuapp.com/"
    }
 }




 async function Datos(){

  const {token} = useAuth();
  const  response = await getDatos(token);
  const data= response.data.Datos
  await response;
    console.log(data);
    document.getElementById("first_name").innerHTML= await data.first_name
  const keys = Object.keys(data)
const values= Object.values(data)
  for (let index = 0; index < keys.length; index++) {
    if(keys[index]=="gender"){
      let value=null
      switch (values[index]) {
        case 0:
        value[index] ="Mujer";
        break;
        case 1:
        values[index]="Hombre";
        break;
    }
  }else if (keys[index]=="dob") {
    var res = values[index].split("T") 
    values[index]=res[0];
  }

    document.getElementById(`${keys[index]}`).innerHTML= values[index]

  }


  }

    function buyInsur(token){
      let type=document.getElementById("type").value
      let precio=calcularPrecio(type);
      return buySeguro(token,type,precio).catch(error => {
        console.log("error")
        try{
        let statusl=document.getElementById("statusl");
          statusl.innerHTML=`Algo a ido mal intentelo de nuevo Error:${error.response.status}`;

      }catch{
      }
      });
    };


  function calcularPrecio(type) {
    let edad = document.getElementById("dob").value;
    let pais = document.getElementById("born_in").value;
    let sexo = document.getElementById("gender").value;
    let incremento = 0;
    let precio=0
    switch (type) {
      case 0:
    precio = 10;
        break;
        case 1:
    precio = 20;
        break;
      case 2:
        precio = 30;
            break;
      }

    if (edad == 50 || edad < 30) {
      incremento = incremento + 16.66;
    }
    if (pais !== "España") {
      incremento = incremento + 33.33;
    }
    if (sexo === "Hombre") {
      incremento = incremento + 33.33;
    }
    incremento = (incremento + 100) / 100;
    precio = Math.round(precio * incremento * 100) / 100;

    return precio;
  }
